method.body:
try
{
	var field = this.fields.complementary;
	this.setAttributesForSuggestions(field, result.contents.suggestComplementaryFeederClass);
	var field = this.fields.similar;
	this.setAttributesForSuggestions(field, result.contents.suggestSimilarFeederClass);
	var field = this.fields.upsell;
	this.setAttributesForSuggestions(field, result.contents.suggestUpsellFeederClass);
	
	// Prices synchronisation...
	var rowSynchronizePrices = this.getElementByAnonId('row_synchronizePrices');
	var rowSynchronizePricesFrom = this.getElementByAnonId('row_synchronizePricesFrom');
	if (result.contents.synchronizePrices)
	{
		rowSynchronizePrices.removeAttribute('hidden');
		rowSynchronizePricesFrom.setAttribute('hidden', 'true');
	}
	else
	{
		rowSynchronizePrices.setAttribute('hidden', 'true');
		rowSynchronizePricesFrom.removeAttribute('hidden');
		var field = this.fields.synchronizePricesFrom;
		field.replaceItems({cmpref: 'modules_catalog/declinationsbyproduct', productId: this.documentEditor.documentid});
	}
}
catch (e)
{
	wCore.error("declinedproduct.propertiespanel.fireInitializeCompleteExtend", [result], e);
}

var productId = this.documentEditor.documentid;
wCore.debug('setAttributesForSuggestions feederClass = '+feederClass);
if (productId && feederClass && feederClass != 'none')
{
	field.setAttribute('productId', productId);
	field.setAttribute('defaultFeederClass', feederClass);
	field.setAttribute('hideSuggestionButton', false);
}
else
{
	field.removeAttribute('productId');
	field.removeAttribute('defaultFeederClass');
	field.setAttribute('hideSuggestionButton', true);
}
