{
	var checkboxes = this.mCheckboxes;
	for (var i = 0; i < checkboxes.length; i++)
	{
		checkboxes[i].checked = false;
	}
}
catch (e)
{
	wCore.error("cComplementaryDialog.updateResults", [], e);
